import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './UserDashboard.css';

function UserDashboard() {
  const [productos, setProductos] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [categoriaSeleccionada, setCategoriaSeleccionada] = useState('todas');
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [productoSeleccionado, setProductoSeleccionado] = useState(null);
  const [formData, setFormData] = useState({
    cantidad: 1,
    especificaciones: '',
    direccion_entrega: '',
    telefono: ''
  });

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        window.location.href = '/login';
        return;
      }

      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      // Obtener productos
      const productosResponse = await axios.get('http://localhost:5000/productos', { headers });
      setProductos(productosResponse.data);

      // Obtener categorías
      const categoriasResponse = await axios.get('http://localhost:5000/categorias', { headers });
      setCategorias(categoriasResponse.data);

      setLoading(false);
    } catch (err) {
      if (err.response && err.response.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('userRole');
        window.location.href = '/login';
      } else {
        setError('Error al cargar los datos');
      }
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handlePedido = (producto) => {
    setProductoSeleccionado(producto);
    setShowModal(true);
  };

  const handleSubmitPedido = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      const pedidoData = {
        producto_id: productoSeleccionado._id,
        cantidad: formData.cantidad,
        especificaciones: formData.especificaciones,
        direccion_entrega: formData.direccion_entrega,
        telefono: formData.telefono,
        estado: 'pendiente',
        fecha: new Date().toISOString()
      };

      await axios.post('http://localhost:5000/pedidos', pedidoData, { headers });
      
      setShowModal(false);
      setProductoSeleccionado(null);
      setFormData({
        cantidad: 1,
        especificaciones: '',
        direccion_entrega: '',
        telefono: ''
      });
      
      alert('Pedido realizado con éxito');
    } catch (err) {
      setError('Error al realizar el pedido');
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    window.location.href = '/login';
  };

  const productosFiltrados = categoriaSeleccionada === 'todas' 
    ? productos 
    : productos.filter(p => p.categoria === categoriaSeleccionada);

  if (loading) return <div className="loading">Cargando...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="user-dashboard">
      <div className="dashboard-header">
        <h1>Catálogo de Productos</h1>
        <button className="logout-button" onClick={handleLogout}>
          Cerrar Sesión
        </button>
      </div>

      <div className="categorias-filter">
        <select 
          value={categoriaSeleccionada} 
          onChange={(e) => setCategoriaSeleccionada(e.target.value)}
        >
          <option value="todas">Todas las categorías</option>
          {categorias.map(categoria => (
            <option key={categoria._id} value={categoria.nombre}>
              {categoria.nombre}
            </option>
          ))}
        </select>
      </div>

      <div className="productos-grid">
        {productosFiltrados.map(producto => (
          <div key={producto._id} className="producto-card">
            <h3>{producto.nombre}</h3>
            <p className="descripcion">{producto.descripcion}</p>
            <p className="precio">${producto.precio}</p>
            <p className="tiempo-entrega">Tiempo de entrega: {producto.tiempo_entrega}</p>
            <div className="materiales">
              <strong>Materiales disponibles:</strong>
              <ul>
                {producto.materiales_disponibles?.map((material, index) => (
                  <li key={index}>{material}</li>
                ))}
              </ul>
            </div>
            <button 
              className="pedir-button"
              onClick={() => handlePedido(producto)}
            >
              Realizar Pedido
            </button>
          </div>
        ))}
      </div>

      {showModal && productoSeleccionado && (
        <div className="modal">
          <div className="modal-content">
            <h2>Realizar Pedido - {productoSeleccionado.nombre}</h2>
            <form onSubmit={handleSubmitPedido}>
              <div className="form-group">
                <label>Cantidad:</label>
                <input
                  type="number"
                  name="cantidad"
                  value={formData.cantidad}
                  onChange={handleInputChange}
                  min="1"
                  required
                />
              </div>
              <div className="form-group">
                <label>Especificaciones adicionales:</label>
                <textarea
                  name="especificaciones"
                  value={formData.especificaciones}
                  onChange={handleInputChange}
                  placeholder="Detalles adicionales del pedido..."
                />
              </div>
              <div className="form-group">
                <label>Dirección de entrega:</label>
                <input
                  type="text"
                  name="direccion_entrega"
                  value={formData.direccion_entrega}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Teléfono de contacto:</label>
                <input
                  type="tel"
                  name="telefono"
                  value={formData.telefono}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="modal-buttons">
                <button type="submit" className="submit-button">
                  Confirmar Pedido
                </button>
                <button
                  type="button"
                  className="cancel-button"
                  onClick={() => {
                    setShowModal(false);
                    setProductoSeleccionado(null);
                  }}
                >
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default UserDashboard;
