import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './AdminDashboard.css';

function AdminDashboard() {
  const [activeTab, setActiveTab] = useState('productos');
  const [productos, setProductos] = useState([]);
  const [pedidos, setPedidos] = useState([]);
  const [categorias, setCategorias] = useState([]);
  const [filamentos, setFilamentos] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [showCategoriaModal, setShowCategoriaModal] = useState(false);
  const [showFilamentoModal, setShowFilamentoModal] = useState(false);
  const [showCostosModal, setShowCostosModal] = useState(false);
  const [editingProduct, setEditingProduct] = useState(null);
  const [editingCategoria, setEditingCategoria] = useState(null);
  const [editingFilamento, setEditingFilamento] = useState(null);
  const [productoSeleccionado, setProductoSeleccionado] = useState(null);
  const [formData, setFormData] = useState({
    nombre: '',
    descripcion: '',
    precio: '',
    categoria: '',
    stock: '',
    tiempo_entrega: '',
    filamentos: [],
    tamaño_maximo: ''
  });
  const [categoriaFormData, setCategoriaFormData] = useState({
    nombre: '',
    descripcion: ''
  });
  const [filamentoFormData, setFilamentoFormData] = useState({
    nombre: '',
    descripcion: '',
    color: '',
    material: '',
    diametro: '',
    stock: ''
  });
  const [costosData, setCostosData] = useState({
    costo_material: 0,
    costo_energia: 0,
    costo_mantenimiento: 0,
    tiempo_impresion: 0,
    costo_hora: 0,
    margen_ganancia: 30
  });
  const [sugerencias, setSugerencias] = useState({
    tendencias: {
      mes_actual: {
        mes: new Date().getMonth() + 1,
        total_pedidos: 0,
        colores_sugeridos: [],
        eventos: []
      },
      mes_siguiente: {
        mes: (new Date().getMonth() + 2) % 12,
        colores_sugeridos: [],
        eventos: []
      },
      meses_mas_pedidos: []
    },
    stock: {
      stock_bajo: [],
      stock_medio: [],
      stock_alto: [],
      necesidades_inmediatas: [],
      stock_bajo_necesidades: [],
      stock_medio_necesidades: [],
      stock_alto_necesidades: []
    },
    pedidos_pendientes: [],
    resumen: {
      total_pedidos_pendientes: 0
    }
  });

  useEffect(() => {
    fetchData();
  }, [activeTab]);

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        window.location.href = '/login';
        return;
      }

      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      if (activeTab === 'productos') {
        const [productosResponse, categoriasResponse, filamentosResponse] = await Promise.all([
          axios.get('http://localhost:5000/productos', { headers }),
          axios.get('http://localhost:5000/categorias', { headers }),
          axios.get('http://localhost:5000/filamentos', { headers })
        ]);
        setProductos(productosResponse.data);
        setCategorias(categoriasResponse.data);
        setFilamentos(filamentosResponse.data);
      } else if (activeTab === 'pedidos') {
        const response = await axios.get('http://localhost:5000/pedidos', { headers });
        setPedidos(response.data);
      } else if (activeTab === 'categorias') {
        const response = await axios.get('http://localhost:5000/categorias', { headers });
        setCategorias(response.data);
      } else if (activeTab === 'filamentos') {
        const response = await axios.get('http://localhost:5000/filamentos', { headers });
        setFilamentos(response.data);
      } else if (activeTab === 'sugerencias') {
        const response = await axios.get('http://localhost:5000/sugerencias', { headers });
        setSugerencias(response.data);
      }
      setLoading(false);
    } catch (err) {
      if (err.response && err.response.status === 401) {
        localStorage.removeItem('token');
        localStorage.removeItem('userRole');
        window.location.href = '/login';
      } else {
        setError('Error al cargar los datos');
      }
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleCategoriaInputChange = (e) => {
    const { name, value } = e.target;
    setCategoriaFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFilamentoInputChange = (e) => {
    const { name, value } = e.target;
    setFilamentoFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      if (editingProduct) {
        await axios.put(`http://localhost:5000/productos/${editingProduct._id}`, formData, { headers });
      } else {
        await axios.post('http://localhost:5000/productos', formData, { headers });
      }

      setShowModal(false);
      setEditingProduct(null);
      setFormData({
        nombre: '',
        descripcion: '',
        precio: '',
        categoria: '',
        stock: '',
        tiempo_entrega: '',
        filamentos: [],
        tamaño_maximo: ''
      });
      fetchData();
    } catch (err) {
      setError('Error al guardar el producto');
    }
  };

  const handleSubmitCategoria = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      if (editingCategoria) {
        await axios.put(`http://localhost:5000/categorias/${editingCategoria._id}`, categoriaFormData, { headers });
      } else {
        await axios.post('http://localhost:5000/categorias', categoriaFormData, { headers });
      }

      setShowCategoriaModal(false);
      setEditingCategoria(null);
      setCategoriaFormData({
        nombre: '',
        descripcion: ''
      });
      fetchData();
    } catch (err) {
      setError('Error al guardar la categoría');
    }
  };

  const handleSubmitFilamento = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      if (editingFilamento) {
        await axios.put(`http://localhost:5000/filamentos/${editingFilamento._id}`, filamentoFormData, { headers });
      } else {
        await axios.post('http://localhost:5000/filamentos', filamentoFormData, { headers });
      }

      setShowFilamentoModal(false);
      setEditingFilamento(null);
      setFilamentoFormData({
        nombre: '',
        descripcion: '',
        color: '',
        material: '',
        diametro: '',
        stock: ''
      });
      fetchData();
    } catch (err) {
      setError('Error al guardar el filamento');
    }
  };

  const handleSubmitCostos = async (e) => {
    e.preventDefault();
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      await axios.put(`http://localhost:5000/productos/${productoSeleccionado._id}/costos`, costosData, { headers });

      setShowCostosModal(false);
      setProductoSeleccionado(null);
      setCostosData({
        costo_material: 0,
        costo_energia: 0,
        costo_mantenimiento: 0,
        tiempo_impresion: 0,
        costo_hora: 0,
        margen_ganancia: 30
      });
      fetchData();
    } catch (err) {
      setError('Error al guardar los costos');
    }
  };

  const handleCostosChange = (e) => {
    const { name, value } = e.target;
    setCostosData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleEdit = (producto) => {
    setEditingProduct(producto);
    setFormData({
      nombre: producto.nombre,
      descripcion: producto.descripcion,
      precio: producto.precio,
      categoria: producto.categoria,
      stock: producto.stock,
      tiempo_entrega: producto.tiempo_entrega,
      filamentos: producto.filamentos?.map(f => f.nombre) || [],
      tamaño_maximo: producto.tamaño_maximo || ''
    });
    setShowModal(true);
  };

  const handleEditCategoria = (categoria) => {
    setEditingCategoria(categoria);
    setCategoriaFormData({
      nombre: categoria.nombre,
      descripcion: categoria.descripcion
    });
    setShowCategoriaModal(true);
  };

  const handleEditFilamento = (filamento) => {
    setEditingFilamento(filamento);
    setFilamentoFormData({
      nombre: filamento.nombre,
      descripcion: filamento.descripcion,
      color: filamento.color,
      material: filamento.material,
      diametro: filamento.diametro,
      stock: filamento.stock
    });
    setShowFilamentoModal(true);
  };

  const handleDelete = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este producto?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/productos/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        fetchData();
      } catch (err) {
        setError('Error al eliminar el producto');
      }
    }
  };

  const handleDeleteCategoria = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar esta categoría?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/categorias/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        fetchData();
      } catch (err) {
        setError('Error al eliminar la categoría');
      }
    }
  };

  const handleDeleteFilamento = async (id) => {
    if (window.confirm('¿Estás seguro de que deseas eliminar este filamento?')) {
      try {
        const token = localStorage.getItem('token');
        await axios.delete(`http://localhost:5000/filamentos/${id}`, {
          headers: {
            'Authorization': `Bearer ${token}`,
            'Content-Type': 'application/json'
          }
        });
        fetchData();
      } catch (err) {
        setError('Error al eliminar el filamento');
      }
    }
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('userRole');
    window.location.href = '/login';
  };

  const handleUpdateEstado = async (pedidoId, nuevoEstado) => {
    try {
      const token = localStorage.getItem('token');
      const headers = {
        'Authorization': `Bearer ${token}`,
        'Content-Type': 'application/json'
      };

      await axios.put(`http://localhost:5000/pedidos/${pedidoId}/estado`, 
        { estado: nuevoEstado },
        { headers }
      );

      fetchData();
    } catch (err) {
      setError('Error al actualizar el estado del pedido');
    }
  };

  const calcularPrecios = (costos) => {
    const costoTotal = 
      parseFloat(costos.costo_material) +
      parseFloat(costos.costo_energia) +
      parseFloat(costos.costo_mantenimiento) +
      (parseFloat(costos.tiempo_impresion) * parseFloat(costos.costo_hora));

    const precioBase = costoTotal * (1 + (parseFloat(costos.margen_ganancia) / 100));

    return {
      costoTotal,
      precioBase,
      preciosVolumen: {
        '1-5': precioBase,
        '6-10': precioBase * 0.95,
        '11-20': precioBase * 0.90,
        '21+': precioBase * 0.85
      }
    };
  };

  if (loading) return <div className="loading">Cargando...</div>;
  if (error) return <div className="error">{error}</div>;

  return (
    <div className="admin-dashboard">
      <div className="dashboard-header">
        <h1>Panel de Administración</h1>
        <button className="logout-button" onClick={handleLogout}>
          Cerrar Sesión
        </button>
      </div>

      <div className="tabs">
        <button 
          className={`tab-button ${activeTab === 'productos' ? 'active' : ''}`}
          onClick={() => setActiveTab('productos')}
        >
          Productos
        </button>
        <button 
          className={`tab-button ${activeTab === 'pedidos' ? 'active' : ''}`}
          onClick={() => setActiveTab('pedidos')}
        >
          Pedidos
        </button>
        <button 
          className={`tab-button ${activeTab === 'categorias' ? 'active' : ''}`}
          onClick={() => setActiveTab('categorias')}
        >
          Categorías
        </button>
        <button 
          className={`tab-button ${activeTab === 'filamentos' ? 'active' : ''}`}
          onClick={() => setActiveTab('filamentos')}
        >
          Filamentos
        </button>
        <button 
          className={`tab-button ${activeTab === 'sugerencias' ? 'active' : ''}`}
          onClick={() => setActiveTab('sugerencias')}
        >
          Sugerencias
        </button>
      </div>

      {activeTab === 'productos' ? (
        <div className="productos-section">
          <button className="add-button" onClick={() => setShowModal(true)}>
            Agregar Nuevo Producto
          </button>

          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Descripción</th>
                  <th>Precio</th>
                  <th>Categoría</th>
                  <th>Stock</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {productos.map(producto => (
                  <tr key={producto._id}>
                    <td>{producto.nombre}</td>
                    <td>{producto.descripcion}</td>
                    <td>${producto.precio}</td>
                    <td>{producto.categoria}</td>
                    <td>{producto.stock}</td>
                    <td>
                      <button
                        className="edit-button"
                        onClick={() => handleEdit(producto)}
                      >
                        Editar
                      </button>
                      <button
                        className="delete-button"
                        onClick={() => handleDelete(producto._id)}
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : activeTab === 'pedidos' ? (
        <div className="pedidos-section">
          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>ID Pedido</th>
                  <th>Producto</th>
                  <th>Cantidad</th>
                  <th>Cliente</th>
                  <th>Teléfono</th>
                  <th>Dirección</th>
                  <th>Especificaciones</th>
                  <th>Estado</th>
                  <th>Fecha</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {pedidos.map(pedido => (
                  <tr key={pedido._id}>
                    <td>{pedido._id}</td>
                    <td>{pedido.producto?.nombre || 'Producto no disponible'}</td>
                    <td>{pedido.cantidad}</td>
                    <td>{pedido.cliente?.nombre || 'Cliente no disponible'}</td>
                    <td>{pedido.telefono}</td>
                    <td>{pedido.direccion_entrega}</td>
                    <td>{pedido.especificaciones || 'Sin especificaciones'}</td>
                    <td>
                      <select
                        value={pedido.estado}
                        onChange={(e) => handleUpdateEstado(pedido._id, e.target.value)}
                        className={`estado-select ${pedido.estado}`}
                      >
                        <option value="pendiente">Pendiente</option>
                        <option value="en_proceso">En Proceso</option>
                        <option value="enviado">Enviado</option>
                      </select>
                    </td>
                    <td>{new Date(pedido.fecha).toLocaleDateString()}</td>
                    <td>
                      <button
                        className="view-button"
                        onClick={() => {
                          alert(`Detalles del pedido:\n
                            Cliente: ${pedido.cliente?.nombre}\n
                            Teléfono: ${pedido.telefono}\n
                            Dirección: ${pedido.direccion_entrega}\n
                            Especificaciones: ${pedido.especificaciones || 'Sin especificaciones'}`
                          );
                        }}
                      >
                        Ver Detalles
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : activeTab === 'categorias' ? (
        <div className="categorias-section">
          <button className="add-button" onClick={() => setShowCategoriaModal(true)}>
            Agregar Nueva Categoría
          </button>

          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Descripción</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {categorias.map(categoria => (
                  <tr key={categoria._id}>
                    <td>{categoria.nombre}</td>
                    <td>{categoria.descripcion}</td>
                    <td>
                      <button
                        className="edit-button"
                        onClick={() => handleEditCategoria(categoria)}
                      >
                        Editar
                      </button>
                      <button
                        className="delete-button"
                        onClick={() => handleDeleteCategoria(categoria._id)}
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : activeTab === 'filamentos' ? (
        <div className="filamentos-section">
          <button className="add-button" onClick={() => setShowFilamentoModal(true)}>
            Agregar Nuevo Filamento
          </button>

          <div className="table-container">
            <table>
              <thead>
                <tr>
                  <th>Nombre</th>
                  <th>Descripción</th>
                  <th>Color</th>
                  <th>Material</th>
                  <th>Diámetro</th>
                  <th>Stock</th>
                  <th>Acciones</th>
                </tr>
              </thead>
              <tbody>
                {filamentos.map(filamento => (
                  <tr key={filamento._id}>
                    <td>{filamento.nombre}</td>
                    <td>{filamento.descripcion}</td>
                    <td>{filamento.color}</td>
                    <td>{filamento.material}</td>
                    <td>{filamento.diametro}</td>
                    <td>{filamento.stock}</td>
                    <td>
                      <button
                        className="edit-button"
                        onClick={() => handleEditFilamento(filamento)}
                      >
                        Editar
                      </button>
                      <button
                        className="delete-button"
                        onClick={() => handleDeleteFilamento(filamento._id)}
                      >
                        Eliminar
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          </div>
        </div>
      ) : activeTab === 'sugerencias' && sugerencias && (
        <div className="sugerencias-section">
          {/* Alertas de Stock Insuficiente */}
          {sugerencias.alertas_stock && sugerencias.alertas_stock.length > 0 && (
            <div className="sugerencias-card alertas-stock">
              <h3>⚠️ Alertas de Stock Insuficiente</h3>
              <div className="resumen-alertas">
                <p>Hay {sugerencias.alertas_stock.length} pedidos que no pueden ser procesados por falta de stock</p>
              </div>
              {sugerencias.alertas_stock.map((alerta, index) => (
                <div key={index} className="alerta-stock-insuficiente">
                  <div className="alerta-header">
                    <h4>Pedido #{alerta.pedido_id.slice(-4)}</h4>
                    <span className="estado-badge pendiente">Pendiente</span>
                  </div>
                  <div className="alerta-detalles">
                    <p><strong>Producto:</strong> {alerta.producto_nombre}</p>
                    <p><strong>Cantidad:</strong> {alerta.cantidad}</p>
                    <p><strong>Fecha de Entrega:</strong> {new Date(alerta.fecha_entrega).toLocaleDateString()}</p>
                  </div>
                  <div className="filamentos-insuficientes">
                    <h5>Filamentos con Stock Insuficiente:</h5>
                    <ul>
                      {alerta.filamentos_insuficientes.map((filamento, fIndex) => (
                        <li key={fIndex}>
                          <span className="color-filamento" style={{backgroundColor: filamento.color.toLowerCase()}}></span>
                          {filamento.nombre} - 
                          Stock actual: {filamento.stock_actual} / 
                          Necesario: {filamento.cantidad_necesaria}
                        </li>
                      ))}
                    </ul>
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Resumen de Pedidos */}
          <div className="sugerencias-card resumen-pedidos">
            <h3>Resumen de Pedidos</h3>
            <div className="estadisticas-pedidos">
              <div className="estadistica">
                <span className="numero">{sugerencias.resumen.total_pedidos_pendientes}</span>
                <span className="label">Total Pedidos</span>
              </div>
              <div className="estadistica">
                <span className="numero error">{sugerencias.resumen.pedidos_sin_stock}</span>
                <span className="label">Sin Stock Suficiente</span>
              </div>
              <div className="estadistica">
                <span className="numero success">{sugerencias.resumen.pedidos_con_stock}</span>
                <span className="label">Con Stock Suficiente</span>
              </div>
            </div>
          </div>

          {/* Primero mostramos los pedidos pendientes */}
          <div className="sugerencias-card pedidos-pendientes">
            <h3>Pedidos Pendientes y En Proceso</h3>
            {sugerencias.pedidos_pendientes && sugerencias.pedidos_pendientes.length > 0 ? (
              <>
                <div className="resumen-pedidos">
                  <p>Total de pedidos activos: {sugerencias.pedidos_pendientes.length}</p>
                </div>
                <div className="tabla-pedidos">
                  <table>
                    <thead>
                      <tr>
                        <th>ID Pedido</th>
                        <th>Producto</th>
                        <th>Cantidad</th>
                        <th>Fecha Entrega</th>
                        <th>Estado</th>
                        <th>Dirección</th>
                        <th>Especificaciones</th>
                      </tr>
                    </thead>
                    <tbody>
                      {sugerencias.pedidos_pendientes.map((pedido, index) => (
                        <tr key={index} className={`estado-${pedido.estado}`}>
                          <td>#{pedido.pedido_id.slice(-4)}</td>
                          <td>{pedido.producto_nombre}</td>
                          <td>{pedido.cantidad}</td>
                          <td>{new Date(pedido.fecha_entrega).toLocaleDateString()}</td>
                          <td>
                            <span className={`estado-badge ${pedido.estado}`}>
                              {pedido.estado.charAt(0).toUpperCase() + pedido.estado.slice(1)}
                            </span>
                          </td>
                          <td>{pedido.direccion_entrega}</td>
                          <td>{pedido.especificaciones || '-'}</td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                </div>
              </>
            ) : (
              <p className="no-pedidos">No hay pedidos pendientes o en proceso</p>
            )}
          </div>

          <div className="sugerencias-card">
            <h3>Sugerencias para el Mes Actual ({new Date().toLocaleString('es-ES', {month: 'long'})})</h3>
            <div className="eventos-mes">
              <h4>Eventos del Mes:</h4>
              <ul>
                {sugerencias.tendencias.mes_actual.eventos.map((evento, index) => (
                  <li key={index}>{evento}</li>
                ))}
              </ul>
            </div>
            <div className="colores-sugeridos">
              <h4>Colores Sugeridos:</h4>
              <div className="color-chips">
                {sugerencias.tendencias.mes_actual.colores_sugeridos.map((color, index) => (
                  <span key={index} className="color-chip">{color}</span>
                ))}
              </div>
            </div>
          </div>

          <div className="sugerencias-card stock-status">
            <h3>Estado del Stock de Filamentos</h3>

            {/* Stock Bajo */}
            {sugerencias.stock.stock_bajo && sugerencias.stock.stock_bajo.length > 0 && (
              <div className="stock-category bajo">
                <h4>Stock Bajo (0-4 unidades)</h4>
                {sugerencias.stock.stock_bajo.map((filamento, index) => (
                  <div key={index} className="alerta-stock bajo">
                    <span className="urgencia">{filamento.urgencia}</span>
                    <p className="nombre-filamento">{filamento.nombre}</p>
                    <p className="stock-actual">Stock actual: {filamento.stock_actual} unidades</p>
                    <p className="sugerencia">{filamento.sugerencia}</p>
                    <p className="recomendacion">
                      Recomendación: Comprar {filamento.recomendacion_compra} unidades
                    </p>
                  </div>
                ))}
              </div>
            )}

            {/* Stock Medio */}
            {sugerencias.stock.stock_medio && sugerencias.stock.stock_medio.length > 0 && (
              <div className="stock-category medio">
                <h4>Stock Medio (5-10 unidades)</h4>
                {sugerencias.stock.stock_medio.map((filamento, index) => (
                  <div key={index} className="alerta-stock medio">
                    <span className="urgencia">{filamento.urgencia}</span>
                    <p className="nombre-filamento">{filamento.nombre}</p>
                    <p className="stock-actual">Stock actual: {filamento.stock_actual} unidades</p>
                    <p className="sugerencia">{filamento.sugerencia}</p>
                    {filamento.recomendacion_compra > 0 && (
                      <p className="recomendacion">
                        Recomendación: Comprar {filamento.recomendacion_compra} unidades
                      </p>
                    )}
                  </div>
                ))}
              </div>
            )}

            {/* Stock Alto */}
            {sugerencias.stock.stock_alto && sugerencias.stock.stock_alto.length > 0 && (
              <div className="stock-category alto">
                <h4>Stock Alto ({'>'}10 unidades)</h4>
                {sugerencias.stock.stock_alto.map((filamento, index) => (
                  <div key={index} className="alerta-stock alto">
                    <span className="urgencia">{filamento.urgencia}</span>
                    <p className="nombre-filamento">{filamento.nombre}</p>
                    <p className="stock-actual">Stock actual: {filamento.stock_actual} unidades</p>
                    <p className="sugerencia">{filamento.sugerencia}</p>
                  </div>
                ))}
              </div>
            )}

            {/* Necesidades Inmediatas */}
            {sugerencias.stock.necesidades_inmediatas && sugerencias.stock.necesidades_inmediatas.length > 0 && (
              <div className="stock-category critico">
                <h4>¡Necesidades Inmediatas!</h4>
                {sugerencias.stock.necesidades_inmediatas.map((filamento, index) => (
                  <div key={index} className="alerta-stock critico">
                    <span className="urgencia">{filamento.urgencia}</span>
                    <p className="nombre-filamento">{filamento.nombre}</p>
                    <p className="stock-actual">Stock actual: {filamento.stock_actual} unidades</p>
                    <p className="necesidades">Necesarios para pedidos: {filamento.cantidad_necesaria} unidades</p>
                    <p className="stock-restante">Stock restante: {filamento.stock_restante} unidades</p>
                    <p className="sugerencia">{filamento.sugerencia}</p>
                    <div className="pedidos-relacionados">
                      <h5>Pedidos relacionados:</h5>
                      <ul>
                        {filamento.pedidos_relacionados.map((pedido, pidx) => (
                          <li key={pidx}>
                            Pedido #{pedido.pedido_id.slice(-4)} - {pedido.producto} 
                            (Cantidad: {pedido.cantidad})
                            <br/>
                            Fecha entrega: {new Date(pedido.fecha_entrega).toLocaleDateString()}
                          </li>
                        ))}
                      </ul>
                    </div>
                    <p className="recomendacion">
                      Recomendación: Comprar {filamento.recomendacion_compra} unidades
                    </p>
                  </div>
                ))}
              </div>
            )}

            {/* Mensaje si no hay filamentos */}
            {(!sugerencias.stock.stock_bajo || sugerencias.stock.stock_bajo.length === 0) &&
             (!sugerencias.stock.stock_medio || sugerencias.stock.stock_medio.length === 0) &&
             (!sugerencias.stock.stock_alto || sugerencias.stock.stock_alto.length === 0) && (
              <div className="no-filamentos">
                <p>No hay información de filamentos disponible</p>
              </div>
            )}
          </div>

          <div className="sugerencias-card">
            <h3>Próximo Mes</h3>
            <div className="eventos-mes">
              <h4>Eventos Próximos:</h4>
              <ul>
                {sugerencias.tendencias.mes_siguiente.eventos.map((evento, index) => (
                  <li key={index}>{evento}</li>
                ))}
              </ul>
            </div>
            <div className="colores-sugeridos">
              <h4>Colores Sugeridos:</h4>
              <div className="color-chips">
                {sugerencias.tendencias.mes_siguiente.colores_sugeridos.map((color, index) => (
                  <span key={index} className="color-chip">{color}</span>
                ))}
              </div>
            </div>
          </div>

          {/* Resumen de Pedidos Pendientes */}
          <div className="sugerencias-card">
            <h3>Resumen de Pedidos Pendientes</h3>
            <p>Total de pedidos pendientes: {sugerencias.resumen.total_pedidos_pendientes}</p>
            <div className="pedidos-list">
              {sugerencias.pedidos_pendientes.map((pedido, index) => (
                <div key={index} className="pedido-item">
                  <p>Pedido #{pedido.pedido_id.slice(-4)}</p>
                  <p>Producto: {pedido.producto}</p>
                  <p>Cantidad: {pedido.cantidad}</p>
                  <p>Estado: {pedido.estado}</p>
                  <p>Fecha entrega: {new Date(pedido.fecha_entrega).toLocaleDateString()}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {showModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editingProduct ? 'Editar Producto' : 'Nuevo Producto'}</h2>
            <form onSubmit={handleSubmit}>
              <div className="form-group">
                <label>Nombre:</label>
                <input
                  type="text"
                  name="nombre"
                  value={formData.nombre}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Descripción:</label>
                <textarea
                  name="descripcion"
                  value={formData.descripcion}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Precio:</label>
                <input
                  type="number"
                  name="precio"
                  value={formData.precio}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Categoría:</label>
                <select
                  name="categoria"
                  value={formData.categoria}
                  onChange={handleInputChange}
                  required
                >
                  <option value="">Seleccionar categoría</option>
                  {categorias.map(categoria => (
                    <option key={categoria._id} value={categoria.nombre}>
                      {categoria.nombre}
                    </option>
                  ))}
                </select>
              </div>
              <div className="form-group">
                <label>Stock:</label>
                <input
                  type="number"
                  name="stock"
                  value={formData.stock}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Tiempo de Entrega:</label>
                <input
                  type="text"
                  name="tiempo_entrega"
                  value={formData.tiempo_entrega}
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Filamentos:</label>
                <input
                  type="text"
                  name="filamentos"
                  value={formData.filamentos.join(', ')}
                  onChange={(e) => setFormData(prev => ({
                    ...prev,
                    filamentos: e.target.value.split(',').map(f => f.trim())
                  }))}
                  placeholder="Separados por comas"
                />
              </div>
              <div className="form-group">
                <label>Tamaño Máximo:</label>
                <input
                  type="text"
                  name="tamaño_maximo"
                  value={formData.tamaño_maximo}
                  onChange={handleInputChange}
                />
              </div>
              <div className="modal-buttons">
                <button type="submit" className="save-button">
                  {editingProduct ? 'Guardar Cambios' : 'Crear Producto'}
                </button>
                <button
                  type="button"
                  className="cancel-button"
                  onClick={() => {
                    setShowModal(false);
                    setEditingProduct(null);
                  }}
                >
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {showCategoriaModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editingCategoria ? 'Editar Categoría' : 'Nueva Categoría'}</h2>
            <form onSubmit={handleSubmitCategoria}>
              <div className="form-group">
                <label>Nombre:</label>
                <input
                  type="text"
                  name="nombre"
                  value={categoriaFormData.nombre}
                  onChange={handleCategoriaInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Descripción:</label>
                <textarea
                  name="descripcion"
                  value={categoriaFormData.descripcion}
                  onChange={handleCategoriaInputChange}
                  required
                />
              </div>
              <div className="modal-buttons">
                <button type="submit" className="save-button">
                  {editingCategoria ? 'Guardar Cambios' : 'Crear Categoría'}
                </button>
                <button
                  type="button"
                  className="cancel-button"
                  onClick={() => {
                    setShowCategoriaModal(false);
                    setEditingCategoria(null);
                  }}
                >
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {showFilamentoModal && (
        <div className="modal">
          <div className="modal-content">
            <h2>{editingFilamento ? 'Editar Filamento' : 'Nuevo Filamento'}</h2>
            <form onSubmit={handleSubmitFilamento}>
              <div className="form-group">
                <label>Nombre:</label>
                <input
                  type="text"
                  name="nombre"
                  value={filamentoFormData.nombre}
                  onChange={handleFilamentoInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Descripción:</label>
                <textarea
                  name="descripcion"
                  value={filamentoFormData.descripcion}
                  onChange={handleFilamentoInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Color:</label>
                <input
                  type="text"
                  name="color"
                  value={filamentoFormData.color}
                  onChange={handleFilamentoInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Material:</label>
                <input
                  type="text"
                  name="material"
                  value={filamentoFormData.material}
                  onChange={handleFilamentoInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Diámetro:</label>
                <input
                  type="text"
                  name="diametro"
                  value={filamentoFormData.diametro}
                  onChange={handleFilamentoInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Stock:</label>
                <input
                  type="number"
                  name="stock"
                  value={filamentoFormData.stock}
                  onChange={handleFilamentoInputChange}
                  required
                />
              </div>
              <div className="modal-buttons">
                <button type="submit" className="save-button">
                  {editingFilamento ? 'Guardar Cambios' : 'Crear Filamento'}
                </button>
                <button
                  type="button"
                  className="cancel-button"
                  onClick={() => {
                    setShowFilamentoModal(false);
                    setEditingFilamento(null);
                  }}
                >
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {showCostosModal && productoSeleccionado && (
        <div className="modal">
          <div className="modal-content costos-modal">
            <h2>Gestión de Costos - {productoSeleccionado.nombre}</h2>
            <form onSubmit={handleSubmitCostos}>
              <div className="costos-section">
                <h3>Costos Directos</h3>
                <div className="form-group">
                  <label>Costo de Material (por unidad):</label>
                  <input
                    type="number"
                    name="costo_material"
                    value={costosData.costo_material}
                    onChange={handleCostosChange}
                    step="0.01"
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Costo de Energía (por unidad):</label>
                  <input
                    type="number"
                    name="costo_energia"
                    value={costosData.costo_energia}
                    onChange={handleCostosChange}
                    step="0.01"
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Costo de Mantenimiento (por unidad):</label>
                  <input
                    type="number"
                    name="costo_mantenimiento"
                    value={costosData.costo_mantenimiento}
                    onChange={handleCostosChange}
                    step="0.01"
                    required
                  />
                </div>
              </div>

              <div className="costos-section">
                <h3>Tiempo y Costos de Impresión</h3>
                <div className="form-group">
                  <label>Tiempo de Impresión (horas):</label>
                  <input
                    type="number"
                    name="tiempo_impresion"
                    value={costosData.tiempo_impresion}
                    onChange={handleCostosChange}
                    step="0.1"
                    required
                  />
                </div>
                <div className="form-group">
                  <label>Costo por Hora de Impresión:</label>
                  <input
                    type="number"
                    name="costo_hora"
                    value={costosData.costo_hora}
                    onChange={handleCostosChange}
                    step="0.01"
                    required
                  />
                </div>
              </div>

              <div className="costos-section">
                <h3>Margen de Ganancia</h3>
                <div className="form-group">
                  <label>Margen de Ganancia (%):</label>
                  <input
                    type="number"
                    name="margen_ganancia"
                    value={costosData.margen_ganancia}
                    onChange={handleCostosChange}
                    min="0"
                    max="100"
                    required
                  />
                </div>
              </div>

              <div className="precios-preview">
                <h3>Vista Previa de Precios</h3>
                {(() => {
                  const precios = calcularPrecios(costosData);
                  return (
                    <div className="precios-grid">
                      <div className="precio-item">
                        <span className="label">Costo Total:</span>
                        <span className="valor">${precios.costoTotal.toFixed(2)}</span>
                      </div>
                      <div className="precio-item">
                        <span className="label">Precio Base:</span>
                        <span className="valor">${precios.precioBase.toFixed(2)}</span>
                      </div>
                      <div className="precio-item">
                        <span className="label">1-5 unidades:</span>
                        <span className="valor">${precios.preciosVolumen['1-5'].toFixed(2)}</span>
                      </div>
                      <div className="precio-item">
                        <span className="label">6-10 unidades:</span>
                        <span className="valor">${precios.preciosVolumen['6-10'].toFixed(2)}</span>
                      </div>
                      <div className="precio-item">
                        <span className="label">11-20 unidades:</span>
                        <span className="valor">${precios.preciosVolumen['11-20'].toFixed(2)}</span>
                      </div>
                      <div className="precio-item">
                        <span className="label">21+ unidades:</span>
                        <span className="valor">${precios.preciosVolumen['21+'].toFixed(2)}</span>
                      </div>
                    </div>
                  );
                })()}
              </div>

              <div className="modal-buttons">
                <button type="submit" className="save-button">
                  Guardar Costos
                </button>
                <button
                  type="button"
                  className="cancel-button"
                  onClick={() => setShowCostosModal(false)}
                >
                  Cancelar
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}

export default AdminDashboard;
